elements.client_script:
;
(async () => {
    s_widget.setFieldValue('recordId',  s_form.getUniqueValue());
    s_widget.setFieldValue('tableName', s_form.getTableName());
    await s_widget.serverUpdate();

    const indications = s_widget.getFieldValue('indications').map(item => ({ ...item }));
    const noIndicators = s_widget.getFieldValue('noActiveIndicators');

    const widgetItems = [];


    indications.forEach(element => {
  // 100+ красный
        if (element.actualElapsedPercentage >= 100) {
            widgetItems.push({
                color: '#FC0000',
                strokeColor: '#FC0000',
                //percent: "...",
                percent: element.actualElapsedPercentage,
                duration: element.duration,
                title: element.indicatorName,
                subtract: element.subtract,
                stage: element.indicatorStage
              // стадия индикации
            });
        };
  // 75-100 - оранжевый 
        if (element.actualElapsedPercentage >= 75 && element.actualElapsedPercentage <= 99) {
            widgetItems.push({
                color: '#ff9900',
                strokeColor: '#ff9900',
                percent: element.actualElapsedPercentage,
                duration: element.duration,
                title: element.indicatorName,
                subtract: element.subtract,
                stage: element.indicatorStage
            });
        };
  // 50-74 - желтый
        if (element.actualElapsedPercentage >= 50 && element.actualElapsedPercentage <= 74) {
            widgetItems.push({
                color: '#FFD800',
                strokeColor: '#FFD800',
                percent: element.actualElapsedPercentage,
                duration: element.duration,
                title: element.indicatorName,
                subtract: element.subtract,
                stage: element.indicatorStage
            });
        };
  // 0-49 - зеленый
        if (element.actualElapsedPercentage >= 0 && element.actualElapsedPercentage <= 49) {
            widgetItems.push({
                color: '#008000',
                strokeColor: '#008000',
                percent: element.actualElapsedPercentage,
                duration: element.duration,
                title: element.indicatorName,
                subtract: element.subtract,
                stage: element.indicatorStage
            });
        };
    });

    const infoBlock = document.getElementsByClassName('sla-info')[0];
    const renderInfo = (data) => {
        let items = '';
        if (data && data.length > 0) {
            data.forEach((item) => {
                const circumference = 163.362;
                const percentOffset = item.percent > 100 ? 100 : item.percent;
                const dashoffset = circumference - percentOffset / 100 * circumference;
                items += `
                    <div class="sla-info__item">
                        <div class="sla-info__diagram">
                            <div class="sla-info__percent" style="color: ${item.color}">${item.percent}</div>
                            <svg width="64" height="64">
                                <circle stroke="#E3E5E8" stroke-width="12" fill="transparent" r="26" cx="32" cy="32"/>
                                <circle class="sla-info__circle"
                                        style="
                                            stroke-dasharray: ${circumference}, ${circumference};
                                            stroke-dashoffset: ${dashoffset};
                                        "
                                        stroke="${item.strokeColor}"
                                        stroke-width="12"
                                        fill="transparent"
                                        r="26"
                                        cx="32"
                                        cy="32"
                                />
                            </svg>
                        </div>
                        <div class="sla-info__duration" style="color: ${item.color}">${item.duration}</div>
						<div class="sla-info__stage">Стадия SLA: ${item.stage}</div>
                        <div class="sla-info__title" title="${item.title}">${item.title}</div>
                        <div class="sla-info__subtract">${item.subtract}</div>

                    </div>
                `;
            });
        } else {
            items = `<div class="sla-info__empty">${noIndicators}</div>`;
        };

        infoBlock.innerHTML = items;
    };
    document.getElementsByClassName('sla-skeleton')[0].remove();
    renderInfo(widgetItems);
})();

elements.css:
.sla {
    padding: 16px;
    background: #FFFFFF;
    border: 1px solid #D5D8DD;
    border-radius: 4px;
    font-family: 'Open Sans', sans-serif;
    overflow: hidden;
    margin-bottom: 8px;
}

.sla-heading {
    display: flex;
    flex-wrap: wrap;
    margin-bottom: 16px;
}

.sla-heading__title {
    font-weight: 600;
    font-size: 16px;
    line-height: 20px;
    color: #0B0C0E;
    margin-right: auto;
    padding-right: 16px;
}

.sla-heading__date {
    font-weight: 600;
    font-size: 12px;
    line-height: 20px;
    color: #5C6470;
}

.sla-info {
    text-align: center;
    margin-bottom: -16px;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
}

.sla-info__item {
    display: inline-block;
    width: 300px;
     //width: 100%;
    max-width: 100%;
    margin: 0 16px 16px 0;
}

.sla-info__diagram {
    width: 64px;
    height: 64px;
    margin: 0 auto 8px;
    position: relative;
}

.sla-info__percent {
    font-weight: 600;
    font-size: 14px;
    line-height: 20px;
    color: #D5D8DD;
    position: absolute;
    left: 13px;
    right: 13px;
    top: 22px;
}

.sla-info__duration {
    font-weight: 600;
    font-size: 12px;
    line-height: 16px;
    color: #5C6470;
    margin-bottom: 8px;
}

.sla-info__title {
    font-weight: 600;
    font-size: 12px;
    line-height: 16px;
    color: #0B0C0E;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.sla-info__circle {
    transform: rotate(-90deg);
    transform-origin: 50% 50%;
}

.sla-info__stage {
    font-size: 12px;
    line-height: 16px;
    font-weight: 600;
    color: #0B0C0E;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.sla-info__empty {
    min-height: 112px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 14px;
    line-height: 20px;
    margin-bottom: 16px;
    color: #ABB1BA;
}

.sla-info__subtract{
    display: none;
}

@media (max-width: 1360px) {
    .sla-info {
        flex-direction: column;
        align-content: center;
    }
}
@media (max-width: 991px) {
    .sla-info {
        flex-direction: row;
    }
}

@media (max-width: 642px) {
    .sla-info {
        flex-direction: column;
    }
}


@keyframes animateShadow {
    from {
        left: -100%;
    }

    to {
        left: 100%;
    }
}

.sla-skeleton {
    position: relative;
    overflow: hidden;
}

.sla-skeleton:after {
    animation: animateShadow 2s ease-in infinite;
    content: '';
    display: block;
    position: absolute;
    top: -50px;
    bottom: -50px;
    left: -100%;
    width: 44px;
    opacity: .5;
    transform: rotate(10deg) translateX(-50%);
    background: -moz-linear-gradient(left,  rgba(255,255,255,0) 0%, rgba(255,255,255,1) 40%, rgba(255,255,255,1) 60%, rgba(255,255,255,0) 100%);
    background: -webkit-linear-gradient(left,  rgba(255,255,255,0) 0%,rgba(255,255,255,1) 40%,rgba(255,255,255,1) 60%,rgba(255,255,255,0) 100%);
    background: linear-gradient(to right,  rgba(255,255,255,0) 0%,rgba(255,255,255,1) 40%,rgba(255,255,255,1) 60%,rgba(255,255,255,0) 100%);
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#00ffffff', endColorstr='#00ffffff',GradientType=1 );
}

.sla-skeleton__item {
    background: #E3E3E3;
    border-radius: 4px;
    height: 16px;
}

.sla-skeleton__item--title {
    width: 107px;
    height: 20px;
}

.sla-skeleton__item--date {
    width: 77px;
}

.sla-skeleton__item--info-title {
    width: 148px;
    margin: 0 auto;
}

.sla-skeleton__item--info-duration {
    width: 66px;
    margin: 0 auto;
}

