elements.client_script:
/**
 * Главный асинхронный модуль для обработки различных действий в виджете.
 * Реализует действия планирования, коррекции адреса и обработки модальных окон.
 *
 * @returns {void}
 */
(async () => {
  const mapTitleToAction = {};

  /**
   * меняет статус К Планированию.
   *
   * @returns {Promise<void>}
   */
  window.s_widget_custom.schedule = async () => {
    await s_widget.setFieldValue("isloadButton", true);
    await s_widget.setFieldValue("action", "schedule");
    await s_widget.serverUpdate();
    await s_widget.setFieldValue("isloadButton", false);
  };

  /**
   * Планирует событие, запрашивает даты начала и окончания через модальное окно.
   *
   * @returns {Promise<void>}
   */
  window.s_widget_custom.fillDate = async () => {
    if (!mapTitleToAction["Заполнить дату"])
      mapTitleToAction["Заполнить дату"] = {
        action: "fillDate",
        fields: ["plannedEndDatetime", "plannedStartDatetime"],
      };
    await s_widget.setFieldValue("isloadButton", true);
    await renderModal(
      "Заполнить дату",
      `<datetime
    model="data.plannedStartDatetime"
    label="Плановая дата/время начала"
    isMandatory="true"
    beginDate="{data.currentDateTime}"
    endDate="{data.plannedEndDatetime}"
  ></datetime>
  <datetime
    model="data.plannedEndDatetime"
    label="Плановая дата/время окончания"
    isMandatory="true"
    beginDate="{data.plannedStartDatetime}"
  ></datetime>`
    );
    await s_widget.setFieldValue("isloadButton", false);
  };

  /**
   * Обрабатывает случай, когда выезд не требуется, и запрашивает комментарий через модальное окно.
   *
   * @returns {Promise<void>}
   */
  window.s_widget_custom.noCheckoutRequired = async () => {
    if (!mapTitleToAction["Выезд не требуется"])
      mapTitleToAction["Выезд не требуется"] = { action: "noCheckoutRequired", fields: ["comment"] };
    await s_widget.setFieldValue("isloadButton", true);
    await renderModal(
      "Выезд не требуется",
      `<textarea 
    model="data.comment" 
    label="Комментарий"
    isMandatory="true"
    placeholder="Укажите причину" 
></textarea>`
    );
    await s_widget.setFieldValue("isloadButton", false);
  };

  /**
   * Корректирует адрес места назначения, запрашивая данные через модальное окно.
   *
   * @returns {Promise<void>}
   */
  window.s_widget_custom.correctAddress = async () => {
    if (!mapTitleToAction["Скорректировать адрес"]) {
      mapTitleToAction["Скорректировать адрес"] = { action: "correctAddress", fields: ["yandexDestinationAddress"] };
      if (await s_widget.getFieldValue("isNeedGeodata")) {
        mapTitleToAction["Скорректировать адрес"]["fields"].push("lat", "lon");
      }
    }
    await s_widget.setFieldValue("isloadButton", true);
    await renderModal(
      "Скорректировать адрес",
      `
<string isMandatory="true" model="data.yandexDestinationAddress" label="Адрес места назначения (для Яндекс)"></string>
<checkbox 
    model="data.isNeedGeodata" 
    label="Укажите координаты вручную" 
    readOnly = "{data.isMandatoryGeodata}"
></checkbox>
<div simple-if="{data.isNeedGeodata}">
  <string column_type="float" isMandatory="data.isNeedGeodata" model="data.lat" label="Широта"></string>
  <string column_type="float" isMandatory="data.isNeedGeodata" model="data.lon" label="Долгота"></string>
</div>
`
    );
    await s_widget.setFieldValue("isloadButton", false);
  };

  /**
   * Обрабатывает завершение работы с модальным окном.
   *
   * @param {boolean} isUpdate - Указывает, обновление ли это текущей записи или нет.
   * @returns {Promise<void>}
   */
  window.s_widget_custom.modalComplete = async (isUpdate) => {
    if (!isUpdate) {
      s_widget.setFieldValue("isShowModal", false);
      s_widget.removeTemplate("modal-fields");
      s_widget.setFieldValue("modalTitle", "");
      clearFields(mapTitleToAction[await s_widget.getFieldValue("modalTitle")].fields);
      return;
    }
    if (!checkingMandatoryFields(mapTitleToAction[await s_widget.getFieldValue("modalTitle")].fields)) {
      s_form.addErrorMessage("Пожалуйста, заполните пустые поля");
      return;
    }
    await s_widget.setFieldValue("action", mapTitleToAction[await s_widget.getFieldValue("modalTitle")].action);
    await s_widget.serverUpdate();
    s_widget.setFieldValue("isShowModal", false);
    s_widget.removeTemplate("modal-fields");
    s_widget.setFieldValue("modalTitle", "");
  };

  /**
   * Открывает условие сервиса и выполняет обновление сервера.
   *
   * @returns {Promise<void>}
   */
  window.s_widget_custom.openServiceCondition = async () => {
    await s_widget.setFieldValue("isloadButton", true);
    await s_widget.setFieldValue("action", "openServiceCondition");
    await s_widget.serverUpdate();
    await s_widget.setFieldValue("isloadButton", false);
  };
  formOnLoadLogik();
  return;

  /**
   * Проверяет, заполнены ли все обязательные поля.
   *
   * @param {Array<string>} fields - Массив обязательных полей для проверки.
   * @returns {boolean} Возвращает true, если все обязательные поля заполнены, иначе false.
   */
  function checkingMandatoryFields(fields) {
    return fields.every((field) => s_widget.getFieldValue(field));
  }

  /**
   * Очищает значения указанных полей.
   *
   * @param {Array<string>} fields - Массив полей, которые необходимо очистить.
   * @returns {void}
   */
  function clearFields(fields) {
    fields.forEach((field) => {
      s_widget.setFieldValue(field, "");
    });
  }

  /**
   * Отображает модальное окно с заданным заголовком и шаблоном.
   *
   * @param {string} modalTitle - Заголовок модального окна.
   * @param {string} template - HTML шаблон для модального окна.
   * @returns {Promise<void>}
   */
  async function renderModal(modalTitle, template) {
    await s_widget.setFieldValue("modalTitle", modalTitle);
    await s_widget.addTemplate("modal-fields", template, "", "inner");
    await s_widget.setFieldValue("isShowModal", true);
  }

  /**
   * Логика загрузки формы, устанавливающая видимость секции
   * "Координация" в зависимости от состояния планирования.
   *
   * @returns {void}
   */
  async function formOnLoadLogik() {
    if (s_form.isNewRecord()) {
      await s_form.setSectionDisplay("Координация", false);
      return;
    }
    await s_widget.setFieldValue("recordId", s_form.getUniqueValue());
    await s_widget.setFieldValue("tableName", s_form.getTableName());
    await s_widget.setFieldValue("action", "load");
    await s_widget.serverUpdate();
    const planningState = s_widget.getFieldValue("planningState");
    if (!planningState || planningState === "no_planning") {
      await s_form.setSectionDisplay("Координация", false);
      return;
    }
  }
})();

(async () => {
  if (!s_form.getUniqueValue()) return;
  s_widget.setFieldValue("isVisible", false);
  s_widget.setFieldValue("action", "INIT");
  window.matchMedia("(max-width: 576px)").matches
    ? s_widget.setFieldValue("mobileSize", true)
    : s_widget.setFieldValue("desktopSize", true);
  s_widget.setFieldValue("isSkeletonVisible", true);
  s_widget.setFieldValue("recordId", s_form.getUniqueValue());
  s_widget.setFieldValue("tableName", s_form.getTableName());
  await s_widget.serverUpdate();

  s_widget.setFieldValue("action", "");

  if (!window.taskInfoRegistry) {
    window.taskInfoRegistry = new TaskInfoRegistry();
  }

  const taskInfo = new TaskInfo();
  taskInfo.render();

  window.taskInfoRegistry.register(s_widget.getId(), taskInfo);
})();

class TaskInfoRegistry {
  constructor() {
    window.taskInfoInstances = {};
    const config = {
      childList: true,
      subtree: true,
    };
    const body = document.querySelector("body");
    const oldHref = document.location.href;
    const observer = new MutationObserver(function () {
      if (oldHref !== document.location.href) {
        window.removeEventListener("resize", this.renderInstances);
        delete window.taskInfoRegistry;
        delete window.taskInfoInstances;
        observer.disconnect();
      }
    });
    observer.observe(body, config);
    window.addEventListener("resize", this.renderInstances);
  }

  renderInstances() {
    const instances = window.taskInfoInstances;
    for (const id in instances) {
      instances[id].render();
    }
  }

  register(widgetId, taskInfo) {
    window.taskInfoInstances[widgetId] = taskInfo;
  }
}

class TaskInfo {
  constructor() {
    this.viewport = new Viewport();
    this.translations = s_widget.getFieldValue("translations");
    this.columnsAndRowsStructure = s_widget.getFieldValue("columnsAndRowsStructure");
    this.uniqueSelector = s_widget.getFieldValue("uniqueSelector");
    this.container = document.querySelector(`.task-info-list.${this.uniqueSelector}`);
    this.isInfoListShown = false;
    s_widget_custom.toggleTaskInformation = () => this.__toggle();
  }

  render() {
    s_widget.setFieldValue("isSkeletonVisible", false);
    if (this.viewport.resizeToMobile()) {
      this.__renderTaskInformationForMobileSize();
    }
    if (this.viewport.resizeToDesktop()) {
      this.__renderTaskInformationForDesktopSize();
    }
  }

  __isMobileSize() {
    return window.matchMedia("(max-width: 576px)").matches;
  }

  __renderTaskInformationForMobileSize() {
    if (!this.columnsAndRowsStructure.length) {
      return;
    }
    this.container.innerHTML = this.__getTemplateForMobileSize();
    s_widget.setFieldValue("toggleTitle", this.translations.showMore);
    s_widget.setFieldValue("isToggleVisible", true);
    this.container.classList.add("hide");
  }

  __renderTaskInformationForDesktopSize() {
    if (!this.columnsAndRowsStructure.length) {
      return;
    }
    this.container.innerHTML = this.__getTemplateForDesktopSize();
    s_widget.setFieldValue("isToggleVisible", false);
  }

  __getTemplateForMobileSize() {
    const typeHandlers = {
      URL: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      List: (column) => (column.value && column.value !== "notSet" ? this.__getListLinks(column) : column.displayValue),
      Reference: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="http://${document.location.host}/record/${column.reference}/${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      Default: (column) => column.displayValue,
    };
    return this.columnsAndRowsStructure
      .flatMap((row) => {
        return row.map((column) => {
          const { displayLabel, typeColumn } = column;
          const valueContent = (typeHandlers[typeColumn] || typeHandlers.Default)(column);
          return `
                <div class="task-info-list__item">
                    <div class="item__title">${displayLabel}</div>
                    <div class="item__value">${valueContent}</div>
                </div>
            `;
        });
      })
      .join("");
  }

  __getTemplateForDesktopSize() {
    let templateRow = "";
    this.columnsAndRowsStructure.forEach((row) => {
      const numberOfColumnsPerRow = row.length;
      const columnsInARow = row.map((column) => column.columnName).join(" ");
      templateRow += `<div class="task-info-list__row"
            style="grid-template-areas: '${columnsInARow}'; grid-template-columns: repeat(${numberOfColumnsPerRow}, 1fr)" ;>
            ${this.__getTemplateForRow(row)}
        </div>`;
    });
    return templateRow;
  }

  __getTemplateForRow(columns) {
    const typeHandlers = {
      URL: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      List: (column) => (column.value && column.value !== "notSet" ? this.__getListLinks(column) : column.displayValue),
      Reference: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="http://${document.location.host}/record/${column.reference}/${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      Default: (column) => column.displayValue,
    };

    return columns
      .map((column) => {
        const { columnName, displayLabel, typeColumn } = column;
        const valueContent = (typeHandlers[typeColumn] || typeHandlers.Default)(column);
        return `
                <div class="task-info-list__item" style="grid-area: ${columnName};">
                    <div class="item__title">${displayLabel}</div>
                    <div class="item__value">${valueContent}</div>
                </div>
            `;
      })
      .join("");
  }

  __getListLinks(column) {
    const values = column.value.split(",");
    const titles = column.displayValue.split(",");
    const links = [];
    for (let index = 0; index < values.length; index++) {
      links.push(
        `<a href="${`http://${document.location.host}/record/${column.reference}/${values[index]}`}" class="item__title-link">${
          titles[index]
        }</a>`
      );
    }
    return links.join(`<span>; </span>`);
  }

  __toggle() {
    if (this.isInfoListShown) {
      this.__hideInfoList();
    } else {
      this.__showInfoList();
    }
  }

  __showInfoList() {
    const chevronDirection = document.querySelector(`.task-info-toggle__chevron.${this.uniqueSelector}`);
    chevronDirection.classList.add("inverted-chevron");
    s_widget.setFieldValue("toggleTitle", this.translations.showLess);
    this.container.classList.remove("hide");
    this.isInfoListShown = true;
  }

  __hideInfoList() {
    const chevronDirection = document.querySelector(`.task-info-toggle__chevron.${this.uniqueSelector}`);
    chevronDirection.classList.remove("inverted-chevron");
    s_widget.setFieldValue("toggleTitle", this.translations.showMore);
    this.container.classList.add("hide");
    this.isInfoListShown = false;
  }
}

class Viewport {
  static NOT_SET = "not_set";
  static DESKTOP = "desktop";
  static MOBILE = "mobile";

  constructor() {
    this.currentSize = Viewport.NOT_SET;
  }

  resizeToMobile() {
    if (this.__isMobile() && this.currentSize !== Viewport.MOBILE) {
      this.currentSize = Viewport.MOBILE;
      return true;
    }
    return false;
  }

  resizeToDesktop() {
    if (!this.__isMobile() && this.currentSize !== Viewport.DESKTOP) {
      this.currentSize = Viewport.DESKTOP;
      return true;
    }
    return false;
  }

  __isMobile() {
    return window.matchMedia("(max-width: 576px)").matches;
  }
}

