(async () => {
  if (!s_form.getUniqueValue()) return;
  s_widget.setFieldValue("isVisible", false);
  s_widget.setFieldValue("action", "INIT");
  window.matchMedia("(max-width: 576px)").matches
    ? s_widget.setFieldValue("mobileSize", true)
    : s_widget.setFieldValue("desktopSize", true);
  s_widget.setFieldValue("isSkeletonVisible", true);
  s_widget.setFieldValue("recordId", s_form.getUniqueValue());
  s_widget.setFieldValue("tableName", s_form.getTableName());
  await s_widget.serverUpdate();

  s_widget.setFieldValue("action", "");

  if (!window.taskInfoRegistry) {
    window.taskInfoRegistry = new TaskInfoRegistry();
  }

  const taskInfo = new TaskInfo();
  taskInfo.render();

  window.taskInfoRegistry.register(s_widget.getId(), taskInfo);
})();

class TaskInfoRegistry {
  constructor() {
    window.taskInfoInstances = {};
    const config = {
      childList: true,
      subtree: true,
    };
    const body = document.querySelector("body");
    const oldHref = document.location.href;
    const observer = new MutationObserver(function () {
      if (oldHref !== document.location.href) {
        window.removeEventListener("resize", this.renderInstances);
        delete window.taskInfoRegistry;
        delete window.taskInfoInstances;
        observer.disconnect();
      }
    });
    observer.observe(body, config);
    window.addEventListener("resize", this.renderInstances);
  }

  renderInstances() {
    const instances = window.taskInfoInstances;
    for (const id in instances) {
      instances[id].render();
    }
  }

  register(widgetId, taskInfo) {
    window.taskInfoInstances[widgetId] = taskInfo;
  }
}

class TaskInfo {
  constructor() {
    this.viewport = new Viewport();
    this.translations = s_widget.getFieldValue("translations");
    this.columnsAndRowsStructure = s_widget.getFieldValue("columnsAndRowsStructure");
    this.uniqueSelector = s_widget.getFieldValue("uniqueSelector");
    this.container = document.querySelector(`.task-info-list.${this.uniqueSelector}`);
    this.isInfoListShown = false;
    s_widget_custom.toggleTaskInformation = () => this.__toggle();
  }

  render() {
    s_widget.setFieldValue("isSkeletonVisible", false);
    if (this.viewport.resizeToMobile()) {
      this.__renderTaskInformationForMobileSize();
    }
    if (this.viewport.resizeToDesktop()) {
      this.__renderTaskInformationForDesktopSize();
    }
  }

  __isMobileSize() {
    return window.matchMedia("(max-width: 576px)").matches;
  }

  __renderTaskInformationForMobileSize() {
    if (!this.columnsAndRowsStructure.length) {
      return;
    }
    this.container.innerHTML = this.__getTemplateForMobileSize();
    s_widget.setFieldValue("toggleTitle", this.translations.showMore);
    s_widget.setFieldValue("isToggleVisible", true);
    this.container.classList.add("hide");
  }

  __renderTaskInformationForDesktopSize() {
    if (!this.columnsAndRowsStructure.length) {
      return;
    }
    this.container.innerHTML = this.__getTemplateForDesktopSize();
    s_widget.setFieldValue("isToggleVisible", false);
  }

  __getTemplateForMobileSize() {
    const typeHandlers = {
      URL: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      List: (column) => (column.value && column.value !== "notSet" ? this.__getListLinks(column) : column.displayValue),
      Reference: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="http://${document.location.host}/record/${column.reference}/${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      Default: (column) => column.displayValue,
    };
    return this.columnsAndRowsStructure
      .flatMap((row) => {
        return row.map((column) => {
          const { displayLabel, typeColumn } = column;
          const valueContent = (typeHandlers[typeColumn] || typeHandlers.Default)(column);
          return `
                <div class="task-info-list__item">
                    <div class="item__title">${displayLabel}</div>
                    <div class="item__value">${valueContent}</div>
                </div>
            `;
        });
      })
      .join("");
  }

  __getTemplateForDesktopSize() {
    let templateRow = "";
    this.columnsAndRowsStructure.forEach((row) => {
      const numberOfColumnsPerRow = row.length;
      const columnsInARow = row.map((column) => column.columnName).join(" ");
      templateRow += `<div class="task-info-list__row"
            style="grid-template-areas: '${columnsInARow}'; grid-template-columns: repeat(${numberOfColumnsPerRow}, 1fr)" ;>
            ${this.__getTemplateForRow(row)}
        </div>`;
    });
    return templateRow;
  }

  __getTemplateForRow(columns) {
    const typeHandlers = {
      URL: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      List: (column) => (column.value && column.value !== "notSet" ? this.__getListLinks(column) : column.displayValue),
      Reference: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="http://${document.location.host}/record/${column.reference}/${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      Default: (column) => column.displayValue,
    };

    return columns
      .map((column) => {
        const { columnName, displayLabel, typeColumn } = column;
        const valueContent = (typeHandlers[typeColumn] || typeHandlers.Default)(column);
        return `
                <div class="task-info-list__item" style="grid-area: ${columnName};">
                    <div class="item__title">${displayLabel}</div>
                    <div class="item__value">${valueContent}</div>
                </div>
            `;
      })
      .join("");
  }

  __getListLinks(column) {
    const values = column.value.split(",");
    const titles = column.displayValue.split(",");
    const links = [];
    for (let index = 0; index < values.length; index++) {
      links.push(
        `<a href="${`http://${document.location.host}/record/${column.reference}/${values[index]}`}" class="item__title-link">${
          titles[index]
        }</a>`
      );
    }
    return links.join(`<span>; </span>`);
  }

  __toggle() {
    if (this.isInfoListShown) {
      this.__hideInfoList();
    } else {
      this.__showInfoList();
    }
  }

  __showInfoList() {
    const chevronDirection = document.querySelector(`.task-info-toggle__chevron.${this.uniqueSelector}`);
    chevronDirection.classList.add("inverted-chevron");
    s_widget.setFieldValue("toggleTitle", this.translations.showLess);
    this.container.classList.remove("hide");
    this.isInfoListShown = true;
  }

  __hideInfoList() {
    const chevronDirection = document.querySelector(`.task-info-toggle__chevron.${this.uniqueSelector}`);
    chevronDirection.classList.remove("inverted-chevron");
    s_widget.setFieldValue("toggleTitle", this.translations.showMore);
    this.container.classList.add("hide");
    this.isInfoListShown = false;
  }
}

class Viewport {
  static NOT_SET = "not_set";
  static DESKTOP = "desktop";
  static MOBILE = "mobile";

  constructor() {
    this.currentSize = Viewport.NOT_SET;
  }

  resizeToMobile() {
    if (this.__isMobile() && this.currentSize !== Viewport.MOBILE) {
      this.currentSize = Viewport.MOBILE;
      return true;
    }
    return false;
  }

  resizeToDesktop() {
    if (!this.__isMobile() && this.currentSize !== Viewport.DESKTOP) {
      this.currentSize = Viewport.DESKTOP;
      return true;
    }
    return false;
  }

  __isMobile() {
    return window.matchMedia("(max-width: 576px)").matches;
  }
}

elements.css:
.task-info {
    padding: 16px;
    background: #FFFFFF;
    border: 1px solid #D5D8DD;
    border-radius: 4px;
    font-family: 'Open Sans', sans-serif;
    overflow: hidden;
    margin-bottom: 8px;
}

.task-info-heading__title {
    font-weight: 600;
}

.task-info-heading {
    margin-bottom: 16px;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
}

.task-info-heading__state {
    line-height: 20px;
    font-size: 14px;
    font-weight: 600;
    padding: 0 8px;
    border-radius: 4px;
    color: #FFFFFF;
    background: #5C6470;
    flex-grow: 0;
    margin-right: 4px;
}

.task-info-heading__updated-info {
    font-weight: 400;
    font-size: 12px;
    line-height: 16px;
    color: #5C6470;
    padding-top: 4px;
}

.task-info-list__row {
    display: grid;
    grid-gap: 8px;
    margin-top: 8px;
}

.task-info-list__item {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    padding: 8px;
    gap: 4px;
    background: #F7F7F8;
    border-radius: 4px;
    word-break: break-word;
}

.item__title {
    font-weight: 400;
    font-size: 12px;
    line-height: 16px;
    color: #5C6470;
}

.item__value {
    font-weight: 400;
    font-size: 12px;
    line-height: 16px;
    color: #0B0C0E;
}

.task-info-heading-skeleton__state {
    width: 79px;
    height: 20px;
    background: #E3E3E3;
    border-radius: 4px;
}

.task-info-heading-skeleton__update-info {
    width: 148px;
    height: 16px;
    background: #E3E3E3;
    border-radius: 4px;
}

.task-info-skeleton-desktop-list {
    display: grid;
    gap: 8px;
    grid-template-columns: repeat(4, 1fr);
}

.task-info-skeleton-list__item {
    height: 52px;
    background: #E3E3E3;
    border-radius: 4px;
}

@keyframes animateShadow {
    from {
        left: -100%;
    }

    to {
        left: 100%;
    }
}

.task-info-skeleton {
    position: relative;
    overflow: hidden;
}

.task-info-skeleton:after {
    animation: animateShadow 2s ease-in infinite;
    content: '';
    display: block;
    position: absolute;
    top: -50px;
    bottom: -50px;
    left: -100%;
    width: 44px;
    opacity: .5;
    transform: rotate(10deg) translateX(-50%);
    background: -moz-linear-gradient(left, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) 40%, rgba(255, 255, 255, 1) 60%, rgba(255, 255, 255, 0) 100%);
    background: -webkit-linear-gradient(left, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) 40%, rgba(255, 255, 255, 1) 60%, rgba(255, 255, 255, 0) 100%);
    background: linear-gradient(to right, rgba(255, 255, 255, 0) 0%, rgba(255, 255, 255, 1) 40%, rgba(255, 255, 255, 1) 60%, rgba(255, 255, 255, 0) 100%);
    filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#00ffffff', endColorstr='#00ffffff', GradientType=1);
}

@media screen and (max-width: 576px) {
    .task-info-list {
        display: grid;
        grid-gap: 8px;
    }

    .task-info-skeleton-list__item {
        width: 118px;
        height: 24px;
    }

    .task-info-heading-skeleton__state {
        margin-bottom: 8px;
    }

    .task-info-toggle {
        display: flex;
        margin-top: 16px;
        flex-direction: row;
        align-items: center;
        gap: 7.33px;
        padding: 2px 8px;
        width: fit-content;
        height: 24px;
        background-color: #F5F9FE;
        border-radius: 4px;
        cursor: pointer;
    }

    .task-info-toggle__title {
        font-weight: 600;
        font-size: 14px;
        line-height: 20px;
        color: #0086E5;
    }

    .inverted-chevron {
        transform: rotate(180deg)
    }

    .hide {
        display: none;
    }
}

@media screen and (max-width: 362px) {
    .task-info-heading {
        flex-direction: column;
        align-items: flex-start;
    }

    .task-info-heading__state {
        margin-bottom: 8px;
    }
}

elements.client_script:
(async () => {
  if (!s_form.getUniqueValue()) return;
  s_widget.setFieldValue("isVisible", false);
  s_widget.setFieldValue("action", "INIT");
  window.matchMedia("(max-width: 576px)").matches
    ? s_widget.setFieldValue("mobileSize", true)
    : s_widget.setFieldValue("desktopSize", true);
  s_widget.setFieldValue("isSkeletonVisible", true);
  s_widget.setFieldValue("recordId", s_form.getUniqueValue());
  s_widget.setFieldValue("tableName", s_form.getTableName());
  await s_widget.serverUpdate();

  s_widget.setFieldValue("action", "");

  if (!window.taskInfoRegistry) {
    window.taskInfoRegistry = new TaskInfoRegistry();
  }

  const taskInfo = new TaskInfo();
  taskInfo.render();

  window.taskInfoRegistry.register(s_widget.getId(), taskInfo);
})();

class TaskInfoRegistry {
  constructor() {
    window.taskInfoInstances = {};
    const config = {
      childList: true,
      subtree: true,
    };
    const body = document.querySelector("body");
    const oldHref = document.location.href;
    const observer = new MutationObserver(function () {
      if (oldHref !== document.location.href) {
        window.removeEventListener("resize", this.renderInstances);
        delete window.taskInfoRegistry;
        delete window.taskInfoInstances;
        observer.disconnect();
      }
    });
    observer.observe(body, config);
    window.addEventListener("resize", this.renderInstances);
  }

  renderInstances() {
    const instances = window.taskInfoInstances;
    for (const id in instances) {
      instances[id].render();
    }
  }

  register(widgetId, taskInfo) {
    window.taskInfoInstances[widgetId] = taskInfo;
  }
}

class TaskInfo {
  constructor() {
    this.viewport = new Viewport();
    this.translations = s_widget.getFieldValue("translations");
    this.columnsAndRowsStructure = s_widget.getFieldValue("columnsAndRowsStructure");
    this.uniqueSelector = s_widget.getFieldValue("uniqueSelector");
    this.container = document.querySelector(`.task-info-list.${this.uniqueSelector}`);
    this.isInfoListShown = false;
    s_widget_custom.toggleTaskInformation = () => this.__toggle();
  }

  render() {
    s_widget.setFieldValue("isSkeletonVisible", false);
    if (this.viewport.resizeToMobile()) {
      this.__renderTaskInformationForMobileSize();
    }
    if (this.viewport.resizeToDesktop()) {
      this.__renderTaskInformationForDesktopSize();
    }
  }

  __isMobileSize() {
    return window.matchMedia("(max-width: 576px)").matches;
  }

  __renderTaskInformationForMobileSize() {
    if (!this.columnsAndRowsStructure.length) {
      return;
    }
    this.container.innerHTML = this.__getTemplateForMobileSize();
    s_widget.setFieldValue("toggleTitle", this.translations.showMore);
    s_widget.setFieldValue("isToggleVisible", true);
    this.container.classList.add("hide");
  }

  __renderTaskInformationForDesktopSize() {
    if (!this.columnsAndRowsStructure.length) {
      return;
    }
    this.container.innerHTML = this.__getTemplateForDesktopSize();
    s_widget.setFieldValue("isToggleVisible", false);
  }

  __getTemplateForMobileSize() {
    const typeHandlers = {
      URL: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      List: (column) => (column.value && column.value !== "notSet" ? this.__getListLinks(column) : column.displayValue),
      Reference: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="http://${document.location.host}/record/${column.reference}/${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      Default: (column) => column.displayValue,
    };
    return this.columnsAndRowsStructure
      .flatMap((row) => {
        return row.map((column) => {
          const { displayLabel, typeColumn } = column;
          const valueContent = (typeHandlers[typeColumn] || typeHandlers.Default)(column);
          return `
                <div class="task-info-list__item">
                    <div class="item__title">${displayLabel}</div>
                    <div class="item__value">${valueContent}</div>
                </div>
            `;
        });
      })
      .join("");
  }

  __getTemplateForDesktopSize() {
    let templateRow = "";
    this.columnsAndRowsStructure.forEach((row) => {
      const numberOfColumnsPerRow = row.length;
      const columnsInARow = row.map((column) => column.columnName).join(" ");
      templateRow += `<div class="task-info-list__row"
            style="grid-template-areas: '${columnsInARow}'; grid-template-columns: repeat(${numberOfColumnsPerRow}, 1fr)" ;>
            ${this.__getTemplateForRow(row)}
        </div>`;
    });
    return templateRow;
  }

  __getTemplateForRow(columns) {
    const typeHandlers = {
      URL: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      List: (column) => (column.value && column.value !== "notSet" ? this.__getListLinks(column) : column.displayValue),
      Reference: (column) =>
        column.value && column.value !== "notSet"
          ? `<a href="http://${document.location.host}/record/${column.reference}/${column.value}" class="item__title-link">${column.displayValue}</a>`
          : column.displayValue,
      Default: (column) => column.displayValue,
    };

    return columns
      .map((column) => {
        const { columnName, displayLabel, typeColumn } = column;
        const valueContent = (typeHandlers[typeColumn] || typeHandlers.Default)(column);
        return `
                <div class="task-info-list__item" style="grid-area: ${columnName};">
                    <div class="item__title">${displayLabel}</div>
                    <div class="item__value">${valueContent}</div>
                </div>
            `;
      })
      .join("");
  }

  __getListLinks(column) {
    const values = column.value.split(",");
    const titles = column.displayValue.split(",");
    const links = [];
    for (let index = 0; index < values.length; index++) {
      links.push(
        `<a href="${`http://${document.location.host}/record/${column.reference}/${values[index]}`}" class="item__title-link">${
          titles[index]
        }</a>`
      );
    }
    return links.join(`<span>; </span>`);
  }

  __toggle() {
    if (this.isInfoListShown) {
      this.__hideInfoList();
    } else {
      this.__showInfoList();
    }
  }

  __showInfoList() {
    const chevronDirection = document.querySelector(`.task-info-toggle__chevron.${this.uniqueSelector}`);
    chevronDirection.classList.add("inverted-chevron");
    s_widget.setFieldValue("toggleTitle", this.translations.showLess);
    this.container.classList.remove("hide");
    this.isInfoListShown = true;
  }

  __hideInfoList() {
    const chevronDirection = document.querySelector(`.task-info-toggle__chevron.${this.uniqueSelector}`);
    chevronDirection.classList.remove("inverted-chevron");
    s_widget.setFieldValue("toggleTitle", this.translations.showMore);
    this.container.classList.add("hide");
    this.isInfoListShown = false;
  }
}

class Viewport {
  static NOT_SET = "not_set";
  static DESKTOP = "desktop";
  static MOBILE = "mobile";

  constructor() {
    this.currentSize = Viewport.NOT_SET;
  }

