const category = s_form.getValue("category");
const currentActivity = s_form.getValue("activity");
const simpleAjax = new SimpleAjax();
await simpleAjax.runScript(
  `
const allTabs = [];
const additionalServices = new SimpleRecord('itsm_tchnsrv_additional_services');
additionalServices.query();
while (additionalServices.next()) {
allTabs.push(additionalServices.getDisplayValue('name'))
}
const activity = new SimpleRecord('itsm_tchnsrv_activity');
activity.get('${currentActivity}');
const trueTabs = (activity.getDisplayValue('additional_services')).split(', ');
    setResult(allTabs.filter(el => !trueTabs.includes(el)));
`,
  null,
  (response) => {
    response.data.result.forEach((el) => s_form.setSectionDisplay(el, false));
    s_form.setSectionDisplay("Управление изменениями", category === "change_request");
    // if (s_form.getTableName() === "itsm_inquiry") {
    //   s_form.setSectionDisplay("Мультикарта", true);
    // }
