const relatedList = document.getElementsByClassName(
    "src-components-heading-___styles-module__Heading___lq4HR src-components-heading-___styles-module__sizeH3___ImnNa src-components-heading-___styles-module__isBold___tDmOc src-components-heading-___styles-module__hasMargin___T5iW0"
)[0];
const sectionBar = document.getElementsByClassName(
    'src-components-tabs-slider-___styles-module__List___SQlGC src-components-tabs-slider-___styles-module__TransitionLeft___ry8ia'
)[0];

// Опции для наблюдателя: наблюдаем за элементом, когда 50% его высоты видимы
const observerOptions = {
    threshold: 0.5,
};

// Функция обратного вызова
const handleIntersection = entries => {
    entries.forEach(entry => {
        if (entry.target === relatedList) {
            bottomBar.style.display = entry.isIntersecting ? 'none' : 'flex';
        } else if (entry.target === sectionBar) {
            topBar.style.display = entry.isIntersecting ? 'none' : 'flex';
        }
    });
};

// Создаем наблюдатель
const observer = new IntersectionObserver(handleIntersection, observerOptions);

// Начинаем наблюдение за обоими элементами
observer.observe(relatedList);
observer.observe(sectionBar);

